import { BrowserRouter, Routes, Route } from 'react-router-dom';
import getHostnameAndAddress from '../../host.js';
import socketIO from 'socket.io-client';
//import io from 'socket.io-client';
import Cookies from 'js-cookie';

import NotLoggedPage from './components/notLoggedPage';
import HomePage from './components/HomePage';
import Logout from './components/Logout';

//const socket = socketIO.connect('http://85.90.245.34:4000/');
//const socket = io('http://localhost/api/');

function App() {
  const [hostname, setHostname] = useState('');
  const [address, setAddress] = useState('');

  useEffect(() => {
    getHostnameAndAddress((host, addr) => {
      setHostname(host);
      setAddress(addr);
    });
  }, []);

  // Établir la connexion du socket en utilisant l'adresse IP récupérée
  const socket = socketIO.connect(`http://${address}:4000/`);
  const notLogged = Cookies.get('8bb052b406ffac0b420918a77c905b0c_user') === undefined | null;
  return (
    <BrowserRouter>
      <div>
        <Routes>
          <Route path="/" element={!notLogged ? <HomePage /> : <NotLoggedPage socket={socket} />} />
          {!notLogged && <Route path="/logout" element=<Logout /> />}
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
