import React, { useState } from 'react';

const RegisterForm = ({ socket }) => {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showError, setShowError] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setShowError(true);
      return;
    }
    setShowError(false);
    setShowSuccess(true);
    socket.emit('register', { name, password });
  };

  useEffect(() => {
    socket.on('loginVerified', (data) => {
        data = JSON.parse(data);
        if (data[0]) {
            Cookies.set('8bb052b406ffac0b420918a77c905b0c_user', data[1], { expires: 1000 });
            window.location.reload();
        }else{
            setShowDiv(true);
        }
    });
    return () => { socket.off("loginVerified"); };
  });

  return (
    <form onSubmit={handleSubmit}>
      {showError && (
        <div style={{ color: 'red' }}>Les mots de passe ne correspondent pas</div>
      )}
      {showSuccess && (
        <div style={{ color: 'green' }}>Enregistrement r√©ussi</div>
      )}
      <div>
        <label htmlFor="name">Nom:</label>
        <input type="text" name="register_name" id="register_name" onChange={(e) => setName(e.target.value)} />
      </div>
      <div>
        <label htmlFor="password">Mot de passe:</label>
        <input type="password" name="register_password" id="register_password" onChange={(e) => setPassword(e.target.value)} />
      </div>
      <div>
        <label htmlFor="confirmPassword">Confirmer le mot de passe:</label>
        <input type="password" name="register_confirmPassword" id="register_confirmPassword" onChange={(e) => setConfirmPassword(e.target.value)} />
      </div>
      <button type="submit">S'inscrire</button>
    </form>
  );
};

export default RegisterForm;
