import { BrowserRouter, Routes, Route } from 'react-router-dom';
import socketIO from 'socket.io-client';
import LoginForm from './components/LoginForm';
import Cookies from 'js-cookie';

const socket = socketIO.connect('http://localhost:4000');
function App() {
  const isLogged = Cookies.get('8bb052b406ffac0b420918a77c905b0c_user') !== undefined;
  return (
    <BrowserRouter>
      <div>
        <Routes>
          <Route
        path="/"
        element={isLogged ? <HomePage /> : <LoginForm socket={socket} />}
      />
      {isLogged && <Route path="/home" element={<HomePage />} />}
      {!isLogged && <Route path="*" element={<Navigate to="/" />} />}
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
